Task.FeeRecordSql:
--------------------------------------------ScheduleMap Record Load--------------------------------- DELETE s FROM ScheduleMap s WHERE [Schedule] = {Schedule} AND ScheduleType = {ScheduleType} AND insurance = {Insurance}
INSERT INTO ScheduleMap ( Schedule ,ScheduleType ,Facility ,Insurance ,StartEffectiveDate ,EndEffectiveDate )
SELECT Schedule = {Schedule} ,ScheduleType = {ScheduleType} ,Facility = facility.[Key] ,Insurance = Insurance.[key] ,StartEffectiveDate = {StartDate} ,EndEffectiveDate = {EndDate} FROM Facility ,Insurance WHERE Insurance.[Key] = {Insurance} and Facility.FacilityType = 'Clinic' --------------------------------------------Fee Schedule Record Load--------------------------------- DELETE FSR FROM dbo.FeeScheduleRecord fsr WHERE fsr.schedule LIKE {Schedule}
INSERT INTO dbo.FeeScheduleRecord ( Schedule ,Member ,Filter ,[Priority] ,Amount ) SELECT Schedule = {Schedule} ,Member = Code ,Filter = CASE WHEN Modifier IS NOT NULL THEN '%+' + Modifier + '%' ELSE '%' END ,[Priority] = CASE WHEN Modifier IS NOT NULL THEN 2 ELSE 1 END ,Amount = Amount FROM SourceScheduleData AS srce WHERE srce.PlaceOfService = 'Clinic' and srce.ScheduleName = {Schedule}
